// CORE START
import React, {useEffect} from 'react';
import {useForm} from "react-hook-form";
import {ErrorMessage} from '@hookform/error-message';
// ASSETS START
import LandingPicturePath from "@images/common/landing/ian-gonzalez-1.jpg";
// UI START
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import CircularProgress from '@material-ui/core/CircularProgress';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
import useLoginUser from "./hooks/useLoginUser";
import {useMutation, useQuery} from "react-query";
import HttpClient from "@core/httpClient";
import * as yup from 'yup';
import {ROUTE_API_LOGIN_CHECK} from "@core/routes";
import {Http} from "@material-ui/icons";
// CORE END
// ASSETS END
// UI END

// CONFIG START
const loginSchema = yup.object().shape({
    email: yup.string().email().required(),
    password: yup.string().required()
});
// CONFIG END

// LINKED LOCAL MODULES START
function Copyright() {
    return (
        <Typography variant="body2" color="textSecondary" align="center">
            {'Copyright Â© '}
            <Link color="inherit" href="https://material-ui.com/">
                Drazah
            </Link>{' '}
            {new Date().getFullYear()}
            {'.'}
        </Typography>
    );
}

// LINKED LOCAL MODULES END

// STYLE START
const useStyles = makeStyles((theme) => ({
    root: {
        height: '100vh',
    },
    image: {
        backgroundImage: 'url(' + LandingPicturePath + ')',
        backgroundRepeat: 'no-repeat',
        backgroundColor:
            theme.palette.type === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
        backgroundSize: 'cover',
        backgroundPosition: 'center',
    },
    paper: {
        margin: theme.spacing(8, 4),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main,
    },
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
}));
// STYLE END

// MAIN MODULE START
const SigninPage = () => {

    const classes = useStyles();
    const {register, errors, setError, handleSubmit} = useForm({
        criteriaMode: "all"
    });

    const {data} = useQuery('users',() => {
        return HttpClient.get('https://eternity.test/api/users');
    });

    const loginAction = useMutation(
        loginParams => {
            return HttpClient.post(ROUTE_API_LOGIN_CHECK, loginParams);
        }
    );


    const onSubmit = (loginData) => {
        if (loginSchema.validate(loginData)) {
            loginAction.mutate(loginData);
        }
    }

        return (
            <Grid container component="main" className={classes.root}>
                <CssBaseline/>
                <Grid item xs={false} sm={4} md={7} className={classes.image}/>
                <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
                    <div className={classes.paper}>
                        <Avatar className={classes.avatar}>
                            <LockOutlinedIcon/>
                        </Avatar>
                        <Typography component="h1" variant="h5">
                            Se connecter
                        </Typography>
                        <form className={classes.form} onSubmit={handleSubmit((data) => onSubmit(data))} noValidate>

                            <TextField
                                error={!!errors.email}
                                variant="outlined"
                                margin="normal"
                                required
                                fullWidth
                                id="email"
                                label="Adresse Email"
                                name="email"
                                autoComplete="email"
                                autoFocus
                                inputRef={register({
                                    required: "Ce champs est obligatoire",
                                    pattern: {
                                        value: /(?:[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*|"(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21\x23-\x5b\x5d-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])*")@(?:(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?|\[(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?|[a-z0-9-]*[a-z0-9]:(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21-\x5a\x53-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])+)\])/,
                                        message: "Ce n'est pas un format d'Email valide"

                                    }
                                })}
                            />
                            <ErrorMessage
                                errors={errors}
                                name="email"
                                render={({messages}) => {
                                    const errorArray = [];
                                    if (messages) {
                                        for (const message of Object.entries(messages)) {
                                            errorArray.push(message[1]);
                                        }
                                    }
                                    return messages
                                        ? errorArray.map((message, key) => (
                                            <p key={key}>{message}</p>
                                        ))
                                        : null;
                                }}
                            />
                            <TextField
                                error={!!errors.email}
                                variant="outlined"
                                margin="normal"
                                required
                                fullWidth
                                name="password"
                                label="Password"
                                type="password"
                                id="password"
                                autoComplete="current-password"
                                inputRef={register({
                                    required: "Ce champs est obligatoire",
                                })}
                            />
                            <ErrorMessage
                                errors={errors}
                                name="password"
                                render={({messages}) => {
                                    const errorArray = [];
                                    if (messages) {
                                        for (const message of Object.entries(messages)) {
                                            errorArray.push(message[1]);
                                        }
                                    }
                                    return messages
                                        ? errorArray.map((message, key) => (
                                            <p key={key}>{message}</p>
                                        ))
                                        : null;
                                }}
                            />

                            <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                color="primary"
                                className={classes.submit}
                            >
                                {
                                    (false === false) ? 'Se connecter' : (
                                        <CircularProgress style={{'color': 'white'}}/>
                                    )
                                }

                            </Button>
                            <Box mt={5}>
                                <Copyright/>
                            </Box>
                        </form>
                    </div>
                </Grid>
            </Grid>
        );
    };

// MAIN MODULE END


    export default SigninPage;
